import React, { useState } from "react";
import { Button, Card, ListGroup } from "react-bootstrap";

const ImgCard = ({ image, setImages }) => {
  const [incrementLikes, setIncrementLikes] = useState(image.likes);

  const incLikes = () => {
    console.log(incrementLikes);
    image.likes = image.likes + 1;
    setIncrementLikes(image.likes);
  };

  return (
    <>
      <Card style={{ width: "18rem" }} className="cardimg">
        <Card.Img variant="top" src={image.webformatURL} />
        <Card.Body>
          <Card.Title>{image.tags}</Card.Title>
          <Card.Text>
            <ListGroup horizontal className="list">
              <ListGroup.Item className="list-li">
                views {image.views}
              </ListGroup.Item>
              <ListGroup.Item className="list-li">
                <Button onClick={incLikes}>likes </Button> {image.likes}
              </ListGroup.Item>
              <ListGroup.Item className="list-li">
                comments {image.comments}
              </ListGroup.Item>
            </ListGroup>
          </Card.Text>
        </Card.Body>
      </Card>
    </>
  );
};

export default ImgCard;
